montageDefine("daa4856","vendor/requirejs/tests/config.html",{text:'<!DOCTYPE html><html><head>\n    <title>require.js: Config Test</title>\n    <script src=doh/runner.js></script>\n    <script src=doh/_browserRunner.js></script>\n    <script>\n        function done() {\n            doh.register(\n                        "config",\n                        [\n                            function config(t){\n                                t.is("blue", s.color);\n                                t.is("dimple-blue", d.color);\n                                t.is("You called a function", f());\n                            }\n                        ]\n                    );\n            doh.run();\n        }\n\n        var s, d, f,\n            require = {\n                baseUrl: "./",\n                paths: {\n                    domReady: \'../domReady\'\n                },\n                deps: ["require", "simple", "dimple", "func", "domReady!"],\n                callback: function (require, simple, dimple, func, doc) {\n                    s = simple;\n                    d = dimple;\n                    f = func;\n                    done();\n                }\n            };\n    </script>\n    <script src=../require.js></script>\n</head>\n<body>\n    <h1>require.js: Config Test</h1>\n    <p>Tests require being defined as an object with init and domReady plugin usage in configuration.</p>\n    <p>Check console for messages.</p>\n\n\n</body></html>'});