montageDefine("daa4856","vendor/requirejs/tests/i18n/common.html",{text:'<!DOCTYPE html><html><head>\n    <title>require.js: Common I18N Test</title>\n    <script src=../../require.js></script>\n    <script src=../doh/runner.js></script>\n    <script src=../doh/_browserRunner.js></script>\n    <script>\n        //Allow locale to be set via query args.\n        var locale = null;\n        var query = location.href.split("#")[0].split("?")[1];\n        var match = query && query.match(/locale=([\\w-]+)/);\n        if (match) {\n            locale = match[1];\n        }\n\n        var red = "red";\n        var blue = "blue";\n        if (locale && locale.indexOf("en-us-surfer") != -1) {\n            red = "red, dude";\n        } else if ((locale && locale.indexOf("fr-") != -1)) {\n            red = "rouge";\n            blue = "bleu";\n        }\n        require({\n            locale: locale,\n            baseUrl: "./",\n            paths: {\n                i18n: "../../i18n"\n            }\n        },\n        ["commonA", "commonB"],\n        function(commonA, commonB) {\n            doh.register(\n                "commoni18n",\n                [\n                    function commoni18n(t) {\n                        t.is(red, commonA);\n                        t.is(blue, commonB);\n                    }\n                ]\n            );\n            doh.run();\n        });\n    </script>\n</head>\n<body>\n    <h1>Common i18n bundle test</h1>\n    <p>This page tests for an i18n plugin resource that is specified by two different modules.</p>\n\n\n</body></html>'});