montageDefine("daa4856","vendor/requirejs/tests/workers.html",{text:'<!DOCTYPE html><html><head>\n    <title>require.js: Web Workers Test</title>\n    <script src=doh/runner.js></script>\n    <script src=doh/_browserRunner.js></script>\n    <script>\n        //Start up a web worker and wait for a response from it.\n        var worker = new Worker(\'workers.js\'),\n            d = new doh.Deferred(),\n            count = 0;\n\n        worker.onerror = function () {\n            console.error("WORKER ERROR", arguments);\n        };\n\n        worker.onmessage = function(evt) {\n            if (evt.data.debug) {\n                console.log(evt.data.debug);\n            } else {\n                count += 1;\n    \n                if (count === 1) {\n                    doh.is("blue", evt.data);\n                } else if (count === 2) {\n                    doh.is("green", evt.data);\n                } else if (count === 3) {\n                    doh.is("You called a function", evt.data);\n                } else if (count === 4) {\n                    doh.is("blue", evt.data);\n                    d.callback(true);\n                }\n            }\n        };\n\n        doh.register(\n            "webworkers",\n            [\n		{\n		    name: "webworkers",\n		    timeout: 5000,\n		    runTest: function() {\n                        return d;\n                    }\n                }\n            ]\n        );\n\n        doh.run();\n    </script>\n</head>\n<body>\n    <h1>require.js: Web Workers Test</h1>\n    <p>Check console for messages</p>\n\n\n</body></html>'});